import { register } from "../../services/guestsService";
function CreateGuest() {
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await register(data);

      if (result && result.access_token) {
        closeModal();

        Swal.fire({
          position: "center",
          icon: "success",
          title: "Tạo tài khoản thành công!",
          showConfirmButton: false,
          timer: 1500,
        }).then(() => {
          window.location.reload();
        });
      } else {
        const message =
          result && typeof result === "object"
            ? [
                ...new Set(
                  Object.values(result)
                    .flat()
                    .filter((val) => typeof val === "string")
                ),
              ].join("\n")
            : "Đăng ký thất bại. Vui lòng thử lại.";

        Swal.fire({
          icon: "error",
          title: "Đăng ký thất bại",
          text: message,
        });
      }
    } catch (error) {
      Swal.fire({
        icon: "error",
        title: "Lỗi hệ thống",
        text: "Không thể đăng ký. Vui lòng thử lại sau.",
      });
    }
  };
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              <img src="images/icon/dbc5.png" alt="" /> Thêm mới khách hàng
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <div className="book-form inn-com-form db-form">
            <form
              className="col s12 ng-pristine ng-valid"
              onSubmit={handleSubmit}
            >
              <div className="row">
                <div className="input-field col s6">
                  <input type="text" className="validate" />
                  <label>Họ và tên</label>
                </div>
                <div className="input-field col s6">
                  <input type="text" className="validate" />
                  <label>Email</label>
                </div>
              </div>
              <div className="row">
                <div className="input-field col s6">
                  <input type="text" className="validate" />
                  <label>Mật khẩu</label>
                </div>
                <div className="input-field col s6">
                  <input type="text" className="validate" />
                  <label>Xác nhận mật khẩu</label>
                </div>
              </div>
              <div className="row">
                <div className="input-field col s8"></div>
                <div className="input-field col s4">
                  <input
                    type="submit"
                    value="Tạo mới khách hàng"
                    className="form-btn"
                  />{" "}
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default CreateGuest;
