import { useState } from "react";
import { storeGuest } from "../../services/guestsService";
import Swal from "sweetalert2/dist/sweetalert2.js";
import "sweetalert2/src/sweetalert2.scss";
function CreateTest(props) {
  // const { setIsCreating } = props;
  // const [data, setData] = useState([]);
  const [image, setImage] = useState(null);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  // const handleChange = (e) => {
  //   const name = e.target.name;
  //   const value = e.target.value;
  //   setData({
  //     ...data,
  //     [name]: value,
  //   });
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("image", image);

    const result = await register(data);
    try {
      const response = await axios.post(
        "http://your-laravel-api-url/api/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      alert("Image uploaded successfully!");
      console.log(response.data);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image.");
    }
  };
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              <img src="images/icon/dbc5.png" alt="" /> Thêm mới khách hàng
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <div className="book-form inn-com-form db-form">
            <form
              onSubmit={handleSubmit}
              className="col s12 ng-pristine ng-valid"
              encType="multipart/form-data"
            >
              <div className="row">
                <div className="input-field col s6">
                  <input name="fullname" type="text" className="validate" />
                  <label>Tiêu</label>
                </div>
                <div className="input-field col s6">
                  <input
                    name="email_address"
                    type="file"
                    className="validate"
                    onChange={handleImageChange}
                    accept="image/*"
                  />
                  {/* <label>Ảnh</label> */}
                </div>
              </div>
              <div className="row">
                <div className="input-field col s8"></div>
                <div className="input-field col s4">
                  <input
                    type="submit"
                    value="Tạo mới khách hàng"
                    className="form-btn"
                  />{" "}
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default CreateTest;
