import { useEffect, useState } from "react";

function UpdateBanner(props) {
  const { setIsCreating, carousel, setEditingCarousel } = props;
  // const [imagePreview, setImagePreview] = useState(null);
  // const [data, setData] = useState(carousel);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setIamge] = useState("");
  const [imagePreview, setImagePreview] = useState(
    `http://127.0.0.1:8000/storage/${carousel.image}`
  );

  useEffect(() => {
    setData(carousel);
    // setImagePreview(`http://127.0.0.1:8000/storage/${carousel.image}`);
  }, [carousel]);

  console.log(carousel);
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              <img src="images/icon/dbc5.png" alt="" /> Cập nhật carousel
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <div className="book-form inn-com-form db-form">
            <form method="POST" className="col s12 ng-pristine ng-valid">
              <div className="row">
                <div className="input-field col s6">
                  <input
                    name="title"
                    type="text"
                    className="validate"
                    value={data.title ?? ""}
                  />
                  {/* <label>Tiêu đề</label> */}
                </div>
                <div className="input-field col s6">
                  <input
                    name="content"
                    type="text"
                    className="validate"
                    value={data.content ?? ""}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                  {/* <label>Nội dung</label> */}
                </div>
              </div>
              <div className="row">
                <div className="input-field col s6">
                  <input
                    name="iamge"
                    type="file"
                    className="validate"
                    id="imageInput"
                  />
                  {/* <label>Ảnh</label> */}
                </div>
                <div className="input-field col s6">
                  {imagePreview && (
                    <img
                      src={imagePreview}
                      alt="Preview"
                      style={{
                        maxWidth: "330px",
                        maxHeight: "330px",
                        objectFit: "contain",
                      }}
                    />
                  )}
                </div>
              </div>
              <div className="row">
                <div className="input-field col s8"></div>
                <div className="input-field col s4">
                  <input
                    type="submit"
                    value="Cập nhật carousel"
                    className="form-btn"
                  />{" "}
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default UpdateBanner;
