function CreateBanner() {
  document
    .getElementById("imageInput")
    .addEventListener("change", function (event) {
      const image = event.target.files[0];
      if (image) {
        const reader = new FileReader();
        reader.onload = function (e) {
          const preview = document.getElementById("previewImage");
          preview.src = e.target.result;
          preview.style.display = "block";
        };
        reader.readAsDataURL(image);
      }
    });
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              <img src="images/icon/dbc5.png" alt="" /> Thêm mới carousel
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <div className="book-form inn-com-form db-form">
            <form method="POST" className="col s12 ng-pristine ng-valid">
              <div className="row">
                <div className="input-field col s6">
                  <input name="title" type="text" className="validate" />
                  <label>Tiêu đề</label>
                </div>
                <div className="input-field col s6">
                  <input name="content" type="text" className="validate" />
                  <label>Nội dung</label>
                </div>
              </div>
              <div className="row">
                <div className="input-field col s6">
                  <input
                    name="iamge"
                    type="file"
                    className="validate"
                    id="imageInput"
                  />
                  {/* <label>Ảnh</label> */}
                </div>
                <div className="input-field col s6">
                  <img
                    src="images/slider/4.jpg"
                    alt=""
                    height={90}
                    width={130}
                  />
                </div>
              </div>
              <div className="row">
                <div className="input-field col s8"></div>
                <div className="input-field col s4">
                  <input
                    type="submit"
                    value="Tạo mới carousel"
                    className="form-btn"
                  />{" "}
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default CreateBanner;
