import { useEffect, useState } from "react";

function UpdateBanner(props) {
  const { setIsCreating, carousel, setEditingCarousel } = props;
  const [imagePreview, setImagePreview] = useState(null);
  const [data, setData] = useState(carousel);

  useEffect(() => {
    setData(carousel);
    setImagePreview();
  }, [carousel]);

  console.log(carousel);
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              <img src="images/icon/dbc5.png" alt="" /> Cập nhật carousel
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <div className="book-form inn-com-form db-form">
            <form method="POST" className="col s12 ng-pristine ng-valid">
              <div className="row">
                <div className="input-field col s6">
                  <input
                    name="title"
                    type="text"
                    className="validate"
                    value={carousel.title}
                  />
                  {/* <label>Tiêu đề</label> */}
                </div>
                <div className="input-field col s6">
                  <input
                    name="content"
                    type="text"
                    className="validate"
                    value={carousel.content}
                  />
                  {/* <label>Nội dung</label> */}
                </div>
              </div>
              <div className="row">
                <div className="input-field col s6">
                  <input
                    name="iamge"
                    type="file"
                    className="validate"
                    id="imageInput"
                  />
                  {/* <label>Ảnh</label> */}
                </div>
                <div className="input-field col s6">
                  {imagePreview && (
                    <img
                      src={imagePreview}
                      alt="Preview"
                      style={{
                        maxWidth: "330px",
                        maxHeight: "330px",
                        objectFit: "contain",
                      }}
                    />
                  )}
                </div>
              </div>
              <div className="row">
                <div className="input-field col s8"></div>
                <div className="input-field col s4">
                  <input
                    type="submit"
                    value="Tạo mới carousel"
                    className="form-btn"
                  />{" "}
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default UpdateBanner;
