import { Edit, Trash2 } from "lucide-react";
import { useEffect, useState } from "react";
import { getAllCarousels } from "../../services/carouselService";
function ListBanner() {
  const [data, setData] = useState([]);

  // Lấy tất cả các Carousel
  const fetchData = async () => {
    try {
      const result = await getAllCarousels(`carousels`);
      setData(result);
    } catch (error) {
      console.error("Lỗi khi fetch khách hàng:", error);
    }
  };

  console.log(data);

  useEffect(() => {
    fetchData();
  }, []);
  return (
    <>
      <div className="db-cent-3">
        <div className="db-cent-table db-com-table">
          <div className="db-title">
            <h3>
              {/* <img src="images/icon/dbc5.png" alt="" /> */}
              Carousel
            </h3>
            <p>
              There are many variations of passages of Lorem Ipsum available,
              but the majority have suffered alteration in some form
            </p>
          </div>
          <button
            style={{
              backgroundColor: "#ec5f67",
              color: "white",
              padding: "8px 16px",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              fontWeight: "bold",
              marginLeft: "520px",
            }}
          >
            THÊM MỚI CAROUSEL
          </button>

          <table
            style={{ marginTop: "20px" }}
            className="bordered responsive-table"
          >
            <thead>
              <tr>
                <th>No</th>
                <th>Tiêu đề</th>
                <th>Nội dung</th>
                <th>Hình ảnh</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => {
                return (
                  <tr>
                    <td>1</td>
                    <td>Carousel 1</td>
                    <td>Nội dung Carousel 1</td>
                    <td>Iamge</td>
                    <td>
                      <Trash2 />
                      <Edit />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default ListBanner;
